// filepath: c:\Users\dhira\OneDrive\Desktop\other\final house rental website\home-rental-app\client\src\pages\HomePage.js
import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import { getAllProperties } from '../api/property';
import PropertyCard from '../components/PropertyCard';
import { useTheme } from '../context/ThemeContext'; // Assuming you have a ThemeContext

const HomePage = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    type: '',
    minPrice: '',
    maxPrice: '',
    bedrooms: '',
    city: '', // Added city filter
  });
  const { theme } = useTheme(); // Example usage of ThemeContext

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const data = await getAllProperties();
        setProperties(data);
        setError('');
      } catch (err) {
        setError('Failed to fetch properties. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchProperties();
  }, []);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const filteredProperties = properties
    .filter(property => 
      property.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      property.location.city.toLowerCase().includes(searchTerm.toLowerCase()) ||
      property.location.country.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .filter(property => filters.type ? property.type === filters.type : true)
    .filter(property => filters.minPrice ? property.pricePerNight >= Number(filters.minPrice) : true)
    .filter(property => filters.maxPrice ? property.pricePerNight <= Number(filters.maxPrice) : true)
    .filter(property => filters.bedrooms ? property.bedrooms >= Number(filters.bedrooms) : true)
    .filter(property => filters.city ? property.location.city.toLowerCase().includes(filters.city.toLowerCase()) : true);


  // Dynamic styling based on theme
  const pageStyle = {
    backgroundColor: theme === 'dark' ? '#333' : '#f8f9fa',
    color: theme === 'dark' ? '#fff' : '#333',
    minHeight: '100vh',
    padding: '20px 0'
  };

  const inputStyle = {
    backgroundColor: theme === 'dark' ? '#555' : '#fff',
    color: theme === 'dark' ? '#fff' : '#333',
    borderColor: theme === 'dark' ? '#777' : '#ced4da'
  };
  
  const selectStyle = {
    ...inputStyle,
    appearance: 'none' // For custom arrow if needed
  };


  return (
    <div style={pageStyle} className="home-page">
      <div className="container mx-auto px-4">
        {/* Hero Section */}
        <section className={`hero-section text-center py-12 md:py-20 rounded-lg mb-12 ${theme === 'dark' ? 'bg-gray-700' : 'bg-indigo-600 text-white'}`}>
          <h1 className="text-4xl md:text-5xl font-bold mb-4">Find Your Perfect Rental</h1>
          <p className="text-lg md:text-xl mb-8">Discover amazing places to stay, from cozy apartments to luxurious villas.</p>
          <div className="max-w-2xl mx-auto">
            <input
              type="text"
              placeholder="Search by name, city, or country..."
              value={searchTerm}
              onChange={handleSearchChange}
              style={inputStyle}
              className="w-full p-3 rounded-md shadow-sm focus:ring-indigo-400 focus:border-indigo-400"
            />
          </div>
        </section>

        {/* Filters Section */}
        <section className={`filters-section mb-10 p-6 rounded-lg shadow-md ${theme === 'dark' ? 'bg-gray-700' : 'bg-white'}`}>
          <h2 className="text-2xl font-semibold mb-4">Filter Properties</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
            <input 
              type="text" 
              name="city" 
              placeholder="City (e.g., Paris)" 
              value={filters.city} 
              onChange={handleFilterChange} 
              style={inputStyle}
              className="p-2 border rounded-md"
            />
            <select name="type" value={filters.type} onChange={handleFilterChange} style={selectStyle} className="p-2 border rounded-md">
              <option value="">All Types</option>
              <option value="Apartment">Apartment</option>
              <option value="House">House</option>
              <option value="Condo">Condo</option>
              <option value="Villa">Villa</option>
              <option value="Cabin">Cabin</option>
            </select>
            <input 
              type="number" 
              name="minPrice" 
              placeholder="Min Price ($)" 
              value={filters.minPrice} 
              onChange={handleFilterChange} 
              style={inputStyle}
              className="p-2 border rounded-md"
            />
            <input 
              type="number" 
              name="maxPrice" 
              placeholder="Max Price ($)" 
              value={filters.maxPrice} 
              onChange={handleFilterChange} 
              style={inputStyle}
              className="p-2 border rounded-md"
            />
            <input 
              type="number" 
              name="bedrooms" 
              placeholder="Min Bedrooms" 
              value={filters.bedrooms} 
              onChange={handleFilterChange} 
              style={inputStyle}
              min="0"
              className="p-2 border rounded-md"
            />
          </div>
        </section>

        {/* Property Listings */}
        <section className="property-listings">
          {loading && <p className="text-center text-xl">Loading properties...</p>}
          {error && <p className="text-center text-red-500 bg-red-100 p-3 rounded">{error}</p>}
          
          {!loading && !error && filteredProperties.length === 0 && (
            <p className="text-center text-xl">No properties match your criteria. Try adjusting your search or filters.</p>
          )}

          {!loading && !error && filteredProperties.length > 0 && (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {filteredProperties.map(property => (
                <PropertyCard key={property._id} property={property} />
              ))}
            </div>
          )}
        </section>

        {/* Call to Action (Optional) */}
        <section className="cta-section text-center py-16">
          <h2 className="text-3xl font-semibold mb-4">Ready to Host?</h2>
          <p className="text-lg mb-6">List your property with us and reach thousands of potential renters.</p>
          <Link 
            to="/rent-your-place" 
            className={`inline-block px-8 py-3 rounded-md text-lg font-medium transition-colors
                        ${theme === 'dark' 
                            ? 'bg-indigo-500 hover:bg-indigo-400 text-white' 
                            : 'bg-green-500 hover:bg-green-600 text-white'}`}
          >
            List Your Property
          </Link>
        </section>
      </div>
    </div>
  );
};

export default HomePage;
