import React, { createContext, useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom'; // Optional: for programmatic navigation on logout
import * as api from '../api/auth'; // Assuming your API functions are here

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate(); // Optional

  const fetchCurrentUser = useCallback(async (currentToken) => {
    if (currentToken) {
      try {
        // Optional: If you have an endpoint to verify token and get user
        // const currentUser = await api.getCurrentUserProfile(currentToken); // You'd need to create this API call
        // For now, we'll assume token presence means basic auth until backend validation
        // If you decode the token on the client, do it carefully and be aware of security.
        // For simplicity, we'll just set a placeholder user if token exists.
        // In a real app, you'd fetch user details from a protected '/me' endpoint.
        // For this example, let's assume if a token exists, we try to parse it (not recommended for sensitive data)
        // or better, have a dedicated endpoint.
        // For now, if token exists, we'll just mark as authenticated and try to get user info later or upon login.
        // This part needs a proper backend endpoint to be secure and reliable.
        // For now, let's just say if there's a token, we assume they *were* logged in.
        // A better approach is to have a `getCurrentUser` function in `api/auth.js`
        // that makes a request to a protected `/api/auth/me` endpoint.

        // Placeholder: If you had a getCurrentUser function in api/auth.js
        // const { user: fetchedUser } = await api.getCurrentUser();
        // setUser(fetchedUser);
        // setIsAuthenticated(true);

        // Simplified: If token exists, assume authenticated for now.
        // Real user data should be fetched on login or from a /me endpoint.
        setIsAuthenticated(true);
        // setUser({ name: "User from token" }); // Placeholder, get real user data
      } catch (error) {
        console.error("Failed to fetch current user or token invalid", error);
        localStorage.removeItem('token');
        setToken(null);
        setUser(null);
        setIsAuthenticated(false);
      }
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      setToken(storedToken);
      // setIsAuthenticated(true); // Basic check, better to verify token with backend
      fetchCurrentUser(storedToken);
    } else {
      setIsLoading(false);
    }
  }, [fetchCurrentUser]);

  const login = async (credentials) => {
    try {
      const data = await api.loginUser(credentials); // api.loginUser should store token in localStorage
      setUser(data.user); // Assuming API returns { token, user }
      setToken(data.token);
      setIsAuthenticated(true);
      localStorage.setItem('token', data.token); // Ensure token is set
      return data;
    } catch (error) {
      console.error("Login failed:", error);
      throw error;
    }
  };

  const register = async (userData) => {
    try {
      const data = await api.registerUser(userData); // api.registerUser might also store token and log in
       // Decide if registration should automatically log in the user
      if (data.token && data.user) {
        setUser(data.user);
        setToken(data.token);
        setIsAuthenticated(true);
        localStorage.setItem('token', data.token);
      }
      return data;
    } catch (error) {
      console.error("Registration failed:", error);
      throw error;
    }
  };

  const logout = () => {
    api.logoutUser(); // Clears token from localStorage
    setUser(null);
    setToken(null);
    setIsAuthenticated(false);
    // navigate('/login'); // Optional: redirect to login page
  };

  return (
    <AuthContext.Provider value={{ user, token, isAuthenticated, isLoading, login, register, logout, setUser, setIsAuthenticated }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
