import React, { useState, useEffect, useContext } from 'react';
import { useAuth } from '../context/AuthContext';
import { Link, Navigate } from 'react-router-dom';
// API calls for user-specific data
import { getUserBookings } from '../api/booking';
import { getUserReviews } from '../api/review';
import { getPropertiesByUser } from '../api/property';
import PropertyCard from '../components/PropertyCard';
import ReviewList from '../components/ReviewList';

const UserDashboard = () => {
  const { user, token, isLoading, logout } = useAuth();
  const [bookings, setBookings] = useState([]);
  const [listings, setListings] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [error, setError] = useState('');
  const [loadingData, setLoadingData] = useState(true);
  useEffect(() => {
    if (!isLoading && !user) {
      // Redirect if not logged in, handled by Navigate component below
      return;
    }
    if (token && user) {
      const fetchData = async () => {
        setLoadingData(true);
        setError('');
        try {
          // Make actual API calls
          console.log('Fetching user dashboard data...');
          
          // Fetch bookings, properties, and reviews in parallel
          const [bookingsData, listingsData, reviewsData] = await Promise.allSettled([
            getUserBookings(user._id, token).catch(err => {
              console.error('Error fetching bookings:', err);
              return [];
            }),
            getPropertiesByUser(token).catch(err => {
              console.error('Error fetching properties:', err);
              return [];
            }),
            getUserReviews(user._id, token).catch(err => {
              console.error('Error fetching reviews:', err);
              return [];
            })
          ]);
          
          // Set data from successful API calls or use empty arrays for failed calls
          setBookings(bookingsData.status === 'fulfilled' ? bookingsData.value : []);
          setListings(listingsData.status === 'fulfilled' ? listingsData.value : []);
          setReviews(reviewsData.status === 'fulfilled' ? reviewsData.value : []);
          
          // If all API calls failed, show a more comprehensive error
          if (
            bookingsData.status === 'rejected' && 
            listingsData.status === 'rejected' && 
            reviewsData.status === 'rejected'
          ) {
            setError('Unable to fetch your data. Please try again later.');
          }
          
          // If we couldn't fetch any data, use mock data as a fallback
          if (
            (!bookingsData.value || bookingsData.value.length === 0) && 
            (!listingsData.value || listingsData.value.length === 0) && 
            (!reviewsData.value || reviewsData.value.length === 0)
          ) {
            // Add mock data if APIs return empty results (for better UX while developing)
            if (bookingsData.status !== 'fulfilled' || bookingsData.value.length === 0) {
              setBookings([{ _id: 'b1', propertyTitle: 'Cozy Apartment', startDate: '2025-06-01', endDate: '2025-06-05', status: 'confirmed' }]);
            }
            
            if (listingsData.status !== 'fulfilled' || listingsData.value.length === 0) {
              setListings([{ _id: 'p1', title: 'My Beach House', status: 'approved', pricePerNight: 150 }]);
            }
            
            if (reviewsData.status !== 'fulfilled' || reviewsData.value.length === 0) {
              setReviews([{ _id: 'r1', propertyTitle: 'Downtown Loft', rating: 5, comment: 'Great place!' }]);
            }
          }
        } catch (err) {
          console.error('Failed to fetch user data:', err);
          setError(err.message || 'Failed to load dashboard data.');
        } finally {
          setLoadingData(false);
        }
      };
      fetchData();
    }
  }, [user, token, isLoading]);

  if (isLoading || loadingData) {
    return <div>Loading dashboard...</div>;
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  // Placeholder handlers
  const handleCancelBooking = (bookingId) => {
    console.log(`Cancel booking ${bookingId}`);
    // Call API: cancelBooking(bookingId, token).then(...)
  };

  const handleEditListing = (listingId) => {
    console.log(`Edit listing ${listingId}`);
    // Navigate to an edit page: navigate(`/listings/${listingId}/edit`);
  };

  const handleDeleteListing = (listingId) => {
    console.log(`Delete listing ${listingId}`);
    // Call API: deleteProperty(listingId, token).then(...)
  };

  return (
    <div className="container mx-auto p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Welcome, {user.name}!</h1>
        <button 
          onClick={logout} 
          className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
        >
          Logout
        </button>
      </div>
      {error && <p className="text-red-500 bg-red-100 p-3 rounded mb-4">Error: {error}</p>}

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* My Bookings Section */}
        <section className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-2xl font-semibold mb-4">My Bookings</h2>
          {bookings.length > 0 ? (
            <ul className="space-y-3">
              {bookings.map(booking => (
                <li key={booking._id} className="p-3 border rounded-md">
                  <p><strong>Property:</strong> {booking.propertyTitle}</p>
                  <p><strong>Dates:</strong> {new Date(booking.startDate).toLocaleDateString()} - {new Date(booking.endDate).toLocaleDateString()}</p>
                  <p><strong>Status:</strong> <span className={`capitalize px-2 py-1 text-xs font-semibold rounded-full ${booking.status === 'confirmed' ? 'bg-green-200 text-green-800' : 'bg-yellow-200 text-yellow-800'}`}>{booking.status}</span></p>
                  {booking.status !== 'cancelled' && booking.status !== 'completed' && (
                     <button onClick={() => handleCancelBooking(booking._id)} className="mt-2 text-sm bg-red-500 hover:bg-red-600 text-white py-1 px-3 rounded">Cancel Booking</button>
                  )}
                </li>
              ))}
            </ul>
          ) : <p>You have no bookings.</p>}
          <Link to="/" className="mt-4 inline-block text-indigo-600 hover:text-indigo-800">Find a place to stay &rarr;</Link>
        </section>

        {/* My Listings Section */}
        <section className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-2xl font-semibold mb-4">My Listings</h2>
          {listings.length > 0 ? (
            <ul className="space-y-3">
              {listings.map(listing => (
                // Replace with <PropertyCard listing={listing} /> if you want to reuse that component
                <li key={listing._id} className="p-3 border rounded-md">
                  <p><strong>Title:</strong> {listing.title}</p>
                  <p><strong>Status:</strong> {listing.status}</p>
                  <p><strong>Price:</strong> ${listing.pricePerNight}/night</p>
                  <div className="mt-2 space-x-2">
                    <button onClick={() => handleEditListing(listing._id)} className="text-sm bg-blue-500 hover:bg-blue-600 text-white py-1 px-3 rounded">Edit</button>
                    <button onClick={() => handleDeleteListing(listing._id)} className="text-sm bg-red-500 hover:bg-red-600 text-white py-1 px-3 rounded">Delete</button>
                  </div>
                </li>
              ))}
            </ul>
          ) : <p>You have no properties listed.</p>}
          <Link to="/rent-your-place" className="mt-4 inline-block text-indigo-600 hover:text-indigo-800">List a new property &rarr;</Link>
        </section>

        {/* My Reviews Section */}
        <section className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-2xl font-semibold mb-4">My Reviews</h2>
          {reviews.length > 0 ? (
            // Replace with <ReviewList reviews={reviews} /> if suitable
            <ul className="space-y-3">
              {reviews.map(review => (
                <li key={review._id} className="p-3 border rounded-md">
                  <p><strong>Property:</strong> {review.propertyTitle}</p>
                  <p><strong>Rating:</strong> {review.rating}/5</p>
                  <p><strong>Comment:</strong> {review.comment}</p>
                  {/* Add edit/delete review options if applicable */}
                </li>
              ))}
            </ul>
          ) : <p>You have not submitted any reviews.</p>}
        </section>
      </div>

      <div className="mt-8 p-6 bg-white rounded-lg shadow">
        <h2 className="text-2xl font-semibold mb-4">Account Details</h2>
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Role:</strong> {user.role}</p>
        <Link to="/profile/edit" className="mt-3 inline-block text-indigo-600 hover:text-indigo-800">Edit Profile &rarr;</Link>
      </div>
    </div>
  );
};

export default UserDashboard;
