// filepath: c:\\Users\\dhira\\OneDrive\\Desktop\\other\\final house rental website\\home-rental-app\\client\\src\\pages\\HomePage.js
import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import { getAllProperties } from '../api/property';
import PropertyCard_imported from '../components/PropertyCard';
import { useTheme } from '../context/ThemeContext';

// --- Start of Diagnostic and Workaround ---
console.log('[HomePage] PropertyCard_imported (raw import):', PropertyCard_imported, 'Type:', typeof PropertyCard_imported);

let ActualPropertyCardComponent = PropertyCard_imported;

if (PropertyCard_imported && typeof PropertyCard_imported === 'object') {
  if (typeof PropertyCard_imported.default === 'function') {
    console.warn('[HomePage] PropertyCard_imported was an object with a .default function. Using .default as the component.');
    ActualPropertyCardComponent = PropertyCard_imported.default;
  } else if (typeof PropertyCard_imported.PropertyCard === 'function') { // Check for .PropertyCard
    console.warn('[HomePage] PropertyCard_imported was an object. Using .PropertyCard as the component (found PropertyCard_imported.PropertyCard).');
    ActualPropertyCardComponent = PropertyCard_imported.PropertyCard;
  } else {
    console.error(
      '[HomePage] CRITICAL: PropertyCard_imported is an object, but neither .default nor .PropertyCard is the component function. Object keys: ',
      Object.keys(PropertyCard_imported).join(', '),
      'Value:',
      PropertyCard_imported
    );
  }
} else if (typeof PropertyCard_imported !== 'function') {
  console.error(
    '[HomePage] CRITICAL: PropertyCard_imported is NOT a function and not an object with a usable .default or .PropertyCard. Type:',
    typeof PropertyCard_imported,
    'Value:',
    PropertyCard_imported
  );
}

console.log('[HomePage] ActualPropertyCardComponent to be used for rendering:', ActualPropertyCardComponent, 'Type:', typeof ActualPropertyCardComponent);
// --- End of Diagnostic and Workaround ---

const HomePage = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    type: '',
    minPrice: '',
    maxPrice: '',
    bedrooms: '',
    locationSearch: '', // New filter for the single location string
  });
    // Try to use theme context with fallback
  let theme = 'light';
  try {
    const themeContext = useTheme(); // Example usage of ThemeContext
    if (themeContext) {
      theme = themeContext.theme;
      console.log('Theme loaded successfully:', theme);
    }
  } catch (err) {
    console.error('Error loading theme:', err);
  }  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        console.log('[HomePage] Fetching properties...');
        const response = await getAllProperties(); 
        console.log('[HomePage] API response:', response);
        
        if (response && response.properties) {
          setProperties(response.properties);
          console.log('[HomePage] Properties set to state:', response.properties);
        } else {
          console.warn('[HomePage] Unexpected API response format:', response);
          if (Array.isArray(response)) {
            setProperties(response);
          } else {
            setProperties([]);
          }
        }
        setError('');
      } catch (err) {
        setError('Failed to fetch properties. Please try again later.');
        console.error('[HomePage] Error fetching properties:', err);
        setProperties([]);
      } finally {
        setLoading(false);
      }
    };
    fetchProperties();
  }, []);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value.toLowerCase());
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };
  const filteredProperties = properties
    .filter(property => {
      // Handle both property formats (name or title)
      if (!property) return false;
      
      const propertyName = property.name || property.title || '';
      const nameMatch = propertyName.toLowerCase().includes(searchTerm);
      
      // Handle different location formats
      let locationText = '';
      if (typeof property.location === 'string') {
        locationText = property.location;
      } else if (property.address) {
        // Handle address object with city and country
        const city = property.address.city || '';
        const country = property.address.country || '';
        locationText = `${city} ${country}`;
      }
      
      const locationMatch = locationText.toLowerCase().includes(searchTerm);
      return nameMatch || locationMatch;
    })
    .filter(property => {
      if (!filters.type) return true;
      return property && property.type === filters.type;
    })
    .filter(property => {
      if (!filters.minPrice) return true;
      // Handle both price formats
      const price = property.price || property.pricePerNight || 0;
      return price >= Number(filters.minPrice);
    })
    .filter(property => {
      if (!filters.maxPrice) return true;
      // Handle both price formats
      const price = property.price || property.pricePerNight || 0;
      return price <= Number(filters.maxPrice);
    })
    .filter(property => {
      if (!filters.bedrooms) return true;
      return property.bedrooms >= Number(filters.bedrooms);
    })
    .filter(property => {
      if (!filters.locationSearch) return true;
      
      // Handle different location formats
      let locationText = '';
      if (typeof property.location === 'string') {
        locationText = property.location;
      } else if (property.address) {
        const city = property.address.city || '';
        const country = property.address.country || '';
        locationText = `${city} ${country}`;
      }
      
      return locationText.toLowerCase().includes(filters.locationSearch.toLowerCase());
    });


  // Dynamic styling based on theme
  return (
    <div className={"min-h-screen bg-gray-50"}>
      <div className="max-w-7xl mx-auto px-4">
        {/* Hero Section */}
        <section className="text-center py-12 md:py-20 rounded-lg mb-12 bg-indigo-600 text-white">
          <h1 className="text-4xl md:text-5xl font-bold mb-4">Find Your Perfect Rental</h1>
          <p className="text-lg md:text-xl mb-8">Discover amazing places to stay, from cozy apartments to luxurious villas.</p>
          <div className="max-w-2xl mx-auto">
            <input
              type="text"
              placeholder="Search by name or location..." // Updated placeholder
              value={searchTerm} // searchTerm is already lowercased by handleSearchChange
              onChange={handleSearchChange} 
              className="w-full p-3 rounded-md shadow-sm focus:ring-indigo-400 focus:border-indigo-400 text-gray-900"
            />
          </div>
        </section>

        {/* Filters Section */}
        <section className="mb-10 p-6 rounded-lg shadow-md bg-white">
          <h2 className="text-2xl font-semibold mb-4 text-gray-800">Filter Properties</h2>
          {/* Adjusted grid columns if city filter is removed/changed */}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            <input 
              type="text" 
              name="locationSearch" // Changed from 'city' to 'locationSearch'
              placeholder="Location (e.g., London)" // Updated placeholder
              value={filters.locationSearch} 
              onChange={handleFilterChange} 
              className="p-2 border rounded-md"
            />
            <select name="type" value={filters.type} onChange={handleFilterChange} className="p-2 border rounded-md">
              <option value="">All Types</option>
              <option value="Apartment">Apartment</option>
              <option value="House">House</option>
              <option value="Condo">Condo</option>
              <option value="Villa">Villa</option>
              <option value="Cabin">Cabin</option>
            </select>
            <input 
              type="number" 
              name="minPrice" 
              placeholder="Min Price ($)" 
              value={filters.minPrice} 
              onChange={handleFilterChange} 
              className="p-2 border rounded-md"
            />
            <input 
              type="number" 
              name="maxPrice" 
              placeholder="Max Price ($)" 
              value={filters.maxPrice} 
              onChange={handleFilterChange} 
              className="p-2 border rounded-md"
            />
            <input 
              type="number" 
              name="bedrooms" 
              placeholder="Min Bedrooms" 
              value={filters.bedrooms} 
              onChange={handleFilterChange} 
              min="0"
              className="p-2 border rounded-md"
            />
          </div>
        </section>

        {/* Property Listings */}
        <section>
          {loading && <p className="text-center text-xl">Loading properties...</p>}
          {error && <p className="text-center text-red-500 bg-red-100 p-3 rounded">{error}</p>}
          {!loading && !error && filteredProperties.length === 0 && (
            <p className="text-center text-xl">No properties match your criteria. Try adjusting your search or filters.</p>
          )}
          {!loading && !error && filteredProperties.length > 0 && (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {/* Diagnostic log: Check PropertyCard before rendering map */}
              {console.log('[HomePage] Rendering. Type of ActualPropertyCardComponent:', typeof ActualPropertyCardComponent, ActualPropertyCardComponent)}
              {filteredProperties.map(property => {
                // Skip rendering if the property is missing a required _id
                if (!property || !property._id) {
                  console.warn('Property missing _id:', property);
                  return null;
                }
                // Diagnostic log: Check PropertyCard just before rendering an instance
                if (typeof ActualPropertyCardComponent !== 'function') {
                    console.error("[HomePage] CRITICAL: ActualPropertyCardComponent is not a function just before rendering instance for property:", property._id, "Type:", typeof ActualPropertyCardComponent, "Value:", ActualPropertyCardComponent);
                    return <div key={property._id}>Error: PropertyCard component is not a valid function.</div>;
                }
                return <ActualPropertyCardComponent key={property._id} property={property} />;
              })}
            </div>
          )}
        </section>

        {/* Call to Action */}
        <section className="text-center py-16">
          <h2 className="text-3xl font-semibold mb-4">Ready to Host?</h2>
          <p className="text-lg mb-6">List your property with us and reach thousands of potential renters.</p>
          <Link 
            to="/rent-your-place" 
            className="inline-block px-8 py-3 rounded-md text-lg font-medium transition-colors bg-green-500 hover:bg-green-600 text-white"
          >
            List Your Property
          </Link>
        </section>
      </div>
    </div>
  );
};

export default HomePage;
