import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { createProperty } from '../api/property'; // Assuming this API function handles image uploads or image URLs

const RentPage = () => {
  const { user, token, isAuthenticated } = useAuth();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    type: 'Apartment', // Default type
    address: '',
    city: '',
    country: '',
    pricePerNight: '',
    bedrooms: '',
    bathrooms: '',
    guests: '',
    amenities: [], // Store as an array of strings
    // images: [], // For file objects or image URLs after upload
  });
  const [amenityInput, setAmenityInput] = useState('');
  // const [imageFiles, setImageFiles] = useState([]); // For handling File objects from input
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  if (!isAuthenticated) {
    // Or show a message prompting login
    navigate('/login?redirect=/rent-your-place');
    return <p>Please log in to list your property.</p>; 
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleAddAmenity = () => {
    if (amenityInput.trim() && !formData.amenities.includes(amenityInput.trim())) {
      setFormData(prev => ({ ...prev, amenities: [...prev.amenities, amenityInput.trim()] }));
      setAmenityInput('');
    }
  };

  const handleRemoveAmenity = (amenityToRemove) => {
    setFormData(prev => ({ 
      ...prev, 
      amenities: prev.amenities.filter(amenity => amenity !== amenityToRemove) 
    }));
  };

  // const handleImageChange = (e) => {
  //   setImageFiles(Array.from(e.target.files));
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess(false);
    setLoading(true);

    // Basic validation
    if (!formData.title || !formData.address || !formData.city || !formData.country || !formData.pricePerNight) {
      setError('Please fill in all required fields: Title, Address, City, Country, Price.');
      setLoading(false);
      return;
    }

    try {
      // Construct the payload. Image handling needs to be decided:
      // 1. Upload images separately, get URLs, then add URLs to propertyData.
      // 2. Send FormData with files if backend handles multipart/form-data directly.
      // For now, assuming image URLs would be part of formData if pre-uploaded, or handled by createProperty.
      
      const propertyData = {
        ...formData,
        owner: user._id, // Attach owner ID
        pricePerNight: Number(formData.pricePerNight),
        bedrooms: Number(formData.bedrooms) || undefined,
        bathrooms: Number(formData.bathrooms) || undefined,
        guests: Number(formData.guests) || undefined,
        location: { // Nesting address details as per typical schema
            street: formData.address,
            city: formData.city,
            country: formData.country,
        }
        // If createProperty expects FormData (e.g., for direct image uploads):
        // const dataToSubmit = new FormData();
        // Object.keys(propertyData).forEach(key => dataToSubmit.append(key, propertyData[key]));
        // imageFiles.forEach(file => dataToSubmit.append('images', file));
      };

      const newProperty = await createProperty(propertyData, token);
      setSuccess(true);
      setLoading(false);
      // Clear form or redirect
      setTimeout(() => {
        navigate(`/properties/${newProperty._id}`); // Navigate to the new property's detail page
      }, 1500);

    } catch (err) {
      console.error('Failed to create property:', err);
      setError(err.message || 'Could not create property. Please try again.');
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4 max-w-2xl">
      <h1 className="text-3xl font-bold mb-6 text-center">List Your Property</h1>
      {success && <p className="bg-green-100 text-green-700 p-3 rounded mb-4">Property listed successfully! Redirecting...</p>}
      {error && <p className="bg-red-100 text-red-500 p-3 rounded mb-4">{error}</p>}
      
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-lg shadow-md space-y-6">
        <div>
          <label htmlFor="title" className="block text-sm font-medium text-gray-700">Title</label>
          <input type="text" name="title" id="title" value={formData.title} onChange={handleChange} required className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500" />
        </div>

        <div>
          <label htmlFor="description" className="block text-sm font-medium text-gray-700">Description</label>
          <textarea name="description" id="description" rows="4" value={formData.description} onChange={handleChange} required className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500"></textarea>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label htmlFor="type" className="block text-sm font-medium text-gray-700">Property Type</label>
            <select name="type" id="type" value={formData.type} onChange={handleChange} className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500">
              <option>Apartment</option>
              <option>House</option>
              <option>Condo</option>
              <option>Villa</option>
              <option>Cabin</option>
              <option>Other</option>
            </select>
          </div>
          <div>
            <label htmlFor="pricePerNight" className="block text-sm font-medium text-gray-700">Price per Night ($)</label>
            <input type="number" name="pricePerNight" id="pricePerNight" value={formData.pricePerNight} onChange={handleChange} required min="1" className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500" />
          </div>
        </div>

        <div>
          <label htmlFor="address" className="block text-sm font-medium text-gray-700">Street Address</label>
          <input type="text" name="address" id="address" value={formData.address} onChange={handleChange} required className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500" />
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label htmlFor="city" className="block text-sm font-medium text-gray-700">City</label>
            <input type="text" name="city" id="city" value={formData.city} onChange={handleChange} required className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500" />
          </div>
          <div>
            <label htmlFor="country" className="block text-sm font-medium text-gray-700">Country</label>
            <input type="text" name="country" id="country" value={formData.country} onChange={handleChange} required className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500" />
          </div>
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div>
            <label htmlFor="bedrooms" className="block text-sm font-medium text-gray-700">Bedrooms</label>
            <input type="number" name="bedrooms" id="bedrooms" value={formData.bedrooms} onChange={handleChange} min="0" className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500" />
          </div>
          <div>
            <label htmlFor="bathrooms" className="block text-sm font-medium text-gray-700">Bathrooms</label>
            <input type="number" name="bathrooms" id="bathrooms" value={formData.bathrooms} onChange={handleChange} min="0" className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500" />
          </div>
          <div>
            <label htmlFor="guests" className="block text-sm font-medium text-gray-700">Max Guests</label>
            <input type="number" name="guests" id="guests" value={formData.guests} onChange={handleChange} min="1" className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500" />
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">Amenities</label>
          <div className="flex items-center mt-1">
            <input 
              type="text" 
              value={amenityInput} 
              onChange={(e) => setAmenityInput(e.target.value)} 
              placeholder="e.g., Wifi, Pool, Air Conditioning"
              className="flex-grow border-gray-300 rounded-l-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
            />
            <button type="button" onClick={handleAddAmenity} className="bg-indigo-500 text-white px-4 py-2 rounded-r-md hover:bg-indigo-600">Add</button>
          </div>
          <ul className="mt-2 space-x-2 space-y-2">
            {formData.amenities.map(amenity => (
              <li key={amenity} className="inline-flex items-center bg-gray-200 text-gray-700 px-2 py-1 rounded-full text-sm">
                {amenity}
                <button type="button" onClick={() => handleRemoveAmenity(amenity)} className="ml-2 text-red-500 hover:text-red-700">&times;</button>
              </li>
            ))}
          </ul>
        </div>

        {/* Placeholder for Image Upload - implement based on backend strategy */}
        {/* <div>
          <label htmlFor="images" className="block text-sm font-medium text-gray-700">Property Images</label>
          <input type="file" name="images" id="images" multiple onChange={handleImageChange} className="mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-indigo-50 file:text-indigo-700 hover:file:bg-indigo-100"/>
          {imageFiles.length > 0 && (
            <ul className="mt-2 text-sm text-gray-600">
              {imageFiles.map(file => <li key={file.name}>{file.name} ({ (file.size / 1024).toFixed(2) } KB)</li>)}
            </ul>
          )}
        </div> */}

        <button 
          type="submit" 
          disabled={loading}
          className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-4 rounded focus:outline-none focus:shadow-outline disabled:opacity-50"
        >
          {loading ? 'Submitting Property...' : 'List My Property'}
        </button>
      </form>
    </div>
  );
};

export default RentPage;
