// filepath: c:\Users\dhira\OneDrive\Desktop\other\final house rental website\home-rental-app\client\src\pages\HomePage.js
import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import { getAllProperties } from "../api/property";
// Fix the import - try named import instead of default import
import PropertyCard from "../components/PropertyCard"; // Changed to default import
import { useTheme } from "../context/ThemeContext";

// SVG components to replace Heroicons
const MagnifyingGlassIcon = (props) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth={1.5}
    stroke="currentColor"
    {...props}
  >
    <path
      strokeLinecap="round"
      strokeLinejoin="round"
      d="M21 21l-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z"
    />
  </svg>
);

const AdjustmentsIcon = (props) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth={1.5}
    stroke="currentColor"
    {...props}
  >
    <path
      strokeLinecap="round"
      strokeLinejoin="round"
      d="M6 13.5V3.75m0 9.75a1.5 1.5 0 0 1 0 3m0-3a1.5 1.5 0 0 0 0 3m0 3.75V16.5m12-3V3.75m0 9.75a1.5 1.5 0 0 1 0 3m0-3a1.5 1.5 0 0 0 0 3m0 3.75V16.5m-6-9V3.75m0 3.75a1.5 1.5 0 0 1 0 3m0-3a1.5 1.5 0 0 0 0 3m0 9.75V10.5"
    />
  </svg>
);

const ChevronDownIcon = (props) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth={1.5}
    stroke="currentColor"
    {...props}
  >
    <path
      strokeLinecap="round"
      strokeLinejoin="round"
      d="M19.5 8.25l-7.5 7.5-7.5-7.5"
    />
  </svg>
);

const ExclamationCircleIcon = (props) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth={1.5}
    stroke="currentColor"
    {...props}
  >
    <path
      strokeLinecap="round"
      strokeLinejoin="round"
      d="M12 9v3.75m9-.75a9 9 0 1 1-18 0 9 9 0 0 1 18 0Zm-9 3.75h.008v.008H12v-.008Z"
    />
  </svg>
);

const PropertyIcon = (props) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    strokeWidth={1.5}
    stroke="currentColor"
    {...props}
  >
    <path
      strokeLinecap="round"
      strokeLinejoin="round"
      d="m2.25 12 8.954-8.955c.44-.439 1.152-.439 1.591 0L21.75 12M4.5 9.75v10.125c0 .621.504 1.125 1.125 1.125H9.75v-4.875c0-.621.504-1.125 1.125-1.125h2.25c.621 0 1.125.504 1.125 1.125V21h4.125c.621 0 1.125-.504 1.125-1.125V9.75M8.25 21h8.25"
    />
  </svg>
);

const HomePage = () => {
  // Add this debug line to check what's being imported
  console.log("PropertyCard import:", PropertyCard);

  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [filters, setFilters] = useState({
    type: "",
    minPrice: "",
    maxPrice: "",
    bedrooms: "",
    locationSearch: "", // New filter for the single location string
  });
  const [showFilters, setShowFilters] = useState(false);

  // Try to use theme context with fallback
  let theme = "light";
  try {
    const themeContext = useTheme(); // Example usage of ThemeContext
    if (themeContext) {
      theme = themeContext.theme;
      console.log("Theme loaded successfully:", theme);
    }
  } catch (err) {
    console.error("Error loading theme:", err);
  }

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        console.log("Fetching properties...");
        // Ensure the data from getAllProperties matches the new structure
        const response = await getAllProperties();
        console.log("API response:", response);

        // The API returns { properties: [...] } format
        // Check if response is in the expected format
        if (response && response.properties) {
          setProperties(response.properties);
          console.log("Properties set to state:", response.properties);
        } else {
          // Handle unexpected response format
          console.warn("Unexpected API response format:", response);
          // If response is array, use it directly
          if (Array.isArray(response)) {
            setProperties(response);
          } else {
            setProperties([]);
          }
        }
        setError("");
      } catch (err) {
        setError("Failed to fetch properties. Please try again later.");
        console.error("Error fetching properties:", err);
        setProperties([]); // Ensure properties is an array in case of error
      } finally {
        setLoading(false);
      }
    };
    fetchProperties();
  }, []);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value.toLowerCase());
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };
  const filteredProperties = properties
    .filter((property) => {
      // Handle both property formats (name or title)
      if (!property) return false;

      const propertyName = property.name || property.title || "";
      const nameMatch = propertyName.toLowerCase().includes(searchTerm);

      // Handle different location formats
      let locationText = "";
      if (typeof property.location === "string") {
        locationText = property.location;
      } else if (property.address) {
        // Handle address object with city and country
        const city = property.address.city || "";
        const country = property.address.country || "";
        locationText = `${city} ${country}`;
      }

      const locationMatch = locationText.toLowerCase().includes(searchTerm);
      return nameMatch || locationMatch;
    })
    .filter((property) => {
      if (!filters.type) return true;
      return property && property.type === filters.type;
    })
    .filter((property) => {
      if (!filters.minPrice) return true;
      // Handle both price formats
      const price = property.price || property.pricePerNight || 0;
      return price >= Number(filters.minPrice);
    })
    .filter((property) => {
      if (!filters.maxPrice) return true;
      // Handle both price formats
      const price = property.price || property.pricePerNight || 0;
      return price <= Number(filters.maxPrice);
    })
    .filter((property) => {
      if (!filters.bedrooms) return true;
      return property.bedrooms >= Number(filters.bedrooms);
    })
    .filter((property) => {
      if (!filters.locationSearch) return true;

      // Handle different location formats
      let locationText = "";
      if (typeof property.location === "string") {
        locationText = property.location;
      } else if (property.address) {
        const city = property.address.city || "";
        const country = property.address.country || "";
        locationText = `${city} ${country}`;
      }

      return locationText
        .toLowerCase()
        .includes(filters.locationSearch.toLowerCase());
    });

  // Dynamic styling based on theme
  return (
    <div
      className={`min-h-screen ${
        theme === "dark"
          ? "bg-gray-900 text-white"
          : "bg-gray-100 text-gray-900"
      } transition-colors duration-300`}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Hero Section */}
        <section className="text-center py-16 md:py-24 rounded-xl mb-12 bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-2xl transform hover:scale-101 transition-transform duration-300">
          <div className="absolute inset-0 bg-black opacity-20 rounded-xl"></div>{" "}
          {/* Subtle overlay */}
          <div className="relative z-10">
            <PropertyIcon className="w-16 h-16 mx-auto mb-4 text-indigo-200" />
            <h1 className="text-4xl sm:text-5xl md:text-6xl font-extrabold mb-6 tracking-tight">
              Find Your <span className="text-yellow-300">Perfect</span> Rental
            </h1>
            <p className="text-lg md:text-xl mb-10 max-w-2xl mx-auto text-indigo-100">
              Discover unique places to stay, from cozy city apartments to
              luxurious countryside villas. Your next adventure starts here.
            </p>
            <div className="max-w-xl mx-auto relative">
              <input
                type="text"
                placeholder="Search by name, location, or keyword..."
                value={searchTerm}
                onChange={handleSearchChange}
                className="w-full p-4 pr-12 rounded-lg shadow-lg focus:ring-4 focus:ring-yellow-400 focus:border-yellow-400 text-gray-900 placeholder-gray-500 text-lg border-0"
              />
              <MagnifyingGlassIcon className="w-6 h-6 text-gray-400 absolute right-4 top-1/2 transform -translate-y-1/2" />
            </div>
          </div>
        </section>

        {/* Filters Toggle Button */}
        <div className="mb-6 text-center md:text-right">
          <button
            onClick={() => setShowFilters(!showFilters)}
            className="inline-flex items-center px-6 py-3 border border-transparent text-base font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-transform transform hover:scale-105"
          >
            <AdjustmentsIcon className="h-5 w-5 mr-2" />
            {showFilters ? "Hide" : "Show"} Filters
            <ChevronDownIcon
              className={`w-5 h-5 ml-2 transform transition-transform duration-200 ${
                showFilters ? "rotate-180" : ""
              }`}
            />
          </button>
        </div>

        {/* Filters Section - Collapsible */}
        {showFilters && (
          <section className="mb-12 p-6 md:p-8 rounded-xl shadow-xl bg-white dark:bg-gray-800 transition-all duration-300 ease-in-out transform scale-100 opacity-100">
            <h2 className="text-2xl md:text-3xl font-semibold mb-6 text-gray-800 dark:text-white flex items-center">
              <AdjustmentsIcon className="w-8 h-8 mr-3 text-indigo-500" />{" "}
              Filter Properties
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {/* Location Search */}
              <div className="relative">
                <label
                  htmlFor="locationSearch"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1"
                >
                  Location
                </label>
                <input
                  type="text"
                  name="locationSearch"
                  id="locationSearch"
                  placeholder="e.g., Paris, New York"
                  value={filters.locationSearch}
                  onChange={handleFilterChange}
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 dark:bg-gray-700 dark:text-white placeholder-gray-400 dark:placeholder-gray-500"
                />
              </div>
              {/* Property Type */}
              <div>
                <label
                  htmlFor="type"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1"
                >
                  Property Type
                </label>
                <select
                  name="type"
                  id="type"
                  value={filters.type}
                  onChange={handleFilterChange}
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 dark:bg-gray-700 dark:text-white"
                >
                  <option value="">All Types</option>
                  <option value="Apartment">Apartment</option>
                  <option value="House">House</option>
                  <option value="Condo">Condo</option>
                  <option value="Villa">Villa</option>
                  <option value="Cabin">Cabin</option>
                </select>
              </div>
              {/* Min Price */}
              <div>
                <label
                  htmlFor="minPrice"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1"
                >
                  Min Price ($)
                </label>
                <input
                  type="number"
                  name="minPrice"
                  id="minPrice"
                  placeholder="0"
                  value={filters.minPrice}
                  onChange={handleFilterChange}
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 dark:bg-gray-700 dark:text-white placeholder-gray-400 dark:placeholder-gray-500"
                />
              </div>
              {/* Max Price */}
              <div>
                <label
                  htmlFor="maxPrice"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1"
                >
                  Max Price ($)
                </label>
                <input
                  type="number"
                  name="maxPrice"
                  id="maxPrice"
                  placeholder="Any"
                  value={filters.maxPrice}
                  onChange={handleFilterChange}
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 dark:bg-gray-700 dark:text-white placeholder-gray-400 dark:placeholder-gray-500"
                />
              </div>
              {/* Bedrooms */}
              <div>
                <label
                  htmlFor="bedrooms"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1"
                >
                  Min Bedrooms
                </label>
                <input
                  type="number"
                  name="bedrooms"
                  id="bedrooms"
                  placeholder="Any"
                  value={filters.bedrooms}
                  onChange={handleFilterChange}
                  min="0"
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 dark:bg-gray-700 dark:text-white placeholder-gray-400 dark:placeholder-gray-500"
                />
              </div>
            </div>
          </section>
        )}
        <section className="pb-16">
          {loading && (
            <div className="text-center py-10">
              {/* Basic Spinner - can be replaced with a more sophisticated skeleton loader */}
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-500 mx-auto"></div>
              <p className="mt-4 text-lg font-medium text-gray-700 dark:text-gray-300">
                Loading properties...
              </p>
            </div>
          )}
          {error && (
            <div className="bg-red-50 dark:bg-red-900 border-l-4 border-red-400 dark:border-red-600 p-6 rounded-md shadow-md my-10">
              <ExclamationCircleIcon className="h-8 w-8 text-red-500 dark:text-red-400 mr-3" />
              <div className="flex items-center">
                <h3 className="text-xl font-semibold text-red-800 dark:text-red-200">
                  Oops! Something went wrong.
                </h3>
                <p className="text-red-700 dark:text-red-300 mt-1">{error}</p>
              </div>
            </div>
          )}
          {!loading && !error && (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
              {filteredProperties.length > 0 ? (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                  {filteredProperties.map((property) => (
                    <PropertyCard key={property._id} property={property} />
                  ))}
                </div>
              ) : (
                <div className="text-center py-12">
                  <MagnifyingGlassIcon className="w-16 h-16 mx-auto text-gray-400 dark:text-gray-500 mb-4" />
                  <h3 className="text-2xl font-semibold text-gray-700 dark:text-gray-200 mb-2">
                    No Properties Found
                  </h3>
                  <p className="text-gray-500 dark:text-gray-400">
                    Try adjusting your search or filters to find what you're
                    looking for.
                  </p>
                </div>
              )}
            </div>
          )}
        </section>

        {/* Call to Action */}
        <section className="pb-16">
          <div className="relative rounded-xl overflow-hidden shadow-xl">
            <div className="absolute inset-0 bg-black opacity-10 rounded-xl"></div>
            <div className="relative z-10 text-center py-16 md:py-20 bg-gradient-to-r from-green-500 to-teal-500 text-white rounded-xl shadow-xl transform hover:scale-101 transition-transform duration-300">
              <h2 className="text-3xl sm:text-4xl font-bold mb-6 tracking-tight">
                Ready to Host?
              </h2>
              <p className="text-lg md:text-xl mb-10 max-w-xl mx-auto text-green-100">
                List your property with HomeScape and reach thousands of
                potential renters worldwide. It's easy and rewarding!
              </p>
              <Link
                to="/rent-your-place"
                className="inline-block px-10 py-4 rounded-lg text-lg font-semibold transition-colors bg-yellow-400 hover:bg-yellow-500 text-gray-900 shadow-md transform hover:scale-105 duration-150 ease-in-out"
              >
                List Your Property Now
              </Link>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default HomePage;
